import { FiUsers, FiUploadCloud, FiFileText } from "react-icons/fi";
import { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const [stats, setStats] = useState({
    agents: 0,
    lists: 0,
    items: 0,
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/dashboard/stats`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setStats(response.data);
      } catch (error) {
        console.error("Error fetching stats:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div>
      <h1 className="text-2xl font-bold mb-8">Dashboard Overview</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <StatCard
          icon={<FiUsers className="text-blue-500" size={24} />}
          title="Total Agents"
          value={stats.agents}
          color="bg-blue-50"
        />
        <StatCard
          icon={<FiUploadCloud className="text-green-500" size={24} />}
          title="Lists Uploaded"
          value={stats.lists}
          color="bg-green-50"
        />
        <StatCard
          icon={<FiFileText className="text-purple-500" size={24} />}
          title="Total Items"
          value={stats.items}
          color="bg-purple-50"
        />
      </div>

      <div className="bg-white rounded-lg shadow-sm p-6">
        <h2 className="text-lg font-semibold mb-4">Recent Activity</h2>
        <div className="text-gray-500">
          Dashboard analytics and recent activities will be displayed here.
        </div>
      </div>
    </div>
  );
}

function StatCard({ icon, title, value, color }) {
  return (
    <div className={`${color} rounded-lg p-6 flex items-center`}>
      <div className="mr-4 p-3 bg-white rounded-full shadow-sm">{icon}</div>
      <div>
        <p className="text-sm text-gray-500">{title}</p>
        <p className="text-2xl font-bold">{value}</p>
      </div>
    </div>
  );
}
