import {
  FiUsers,
  FiUploadCloud,
  FiFileText,
  FiUserPlus,
  FiFile,
} from "react-icons/fi";
import { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const [stats, setStats] = useState({
    agents: 0,
    lists: 0,
    items: 0,
  });
  const [activities, setActivities] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch initial data and set up WebSocket connection
  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("token");

        // Fetch stats and recent activities in parallel
        const [statsRes, activitiesRes] = await Promise.all([
          axios.get(`${process.env.REACT_APP_API_URL}/api/lists/stats`, {
            headers: { Authorization: `Bearer ${token}` },
          }),
          axios.get(`${process.env.REACT_APP_API_URL}/api/activities`, {
            headers: { Authorization: `Bearer ${token}` },
          }),
        ]);

        setStats(statsRes.data.stats);
        setActivities(activitiesRes.data.activities);
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();

    // Set up WebSocket connection for real-time updates
    const ws = new WebSocket(
      `wss://${window.location.host.replace(/^http/, "ws")}/ws`
    );

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.type === "stats_update") {
        setStats(data.payload.stats);
      }
      if (data.type === "activity") {
        setActivities((prev) => [data.payload, ...prev.slice(0, 4)]); // Keep only 5 most recent
      }
    };

    ws.onerror = (error) => {
      console.error("WebSocket error:", error);
    };

    return () => ws.close();
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div>
      <h1 className="text-2xl font-bold mb-8">Dashboard Overview</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <StatCard
          icon={<FiUsers className="text-blue-500" size={24} />}
          title="Total Agents"
          value={stats.agents}
          color="bg-blue-50"
        />
        <StatCard
          icon={<FiUploadCloud className="text-green-500" size={24} />}
          title="Lists Uploaded"
          value={stats.lists}
          color="bg-green-50"
        />
        <StatCard
          icon={<FiFileText className="text-purple-500" size={24} />}
          title="Total Items"
          value={stats.items}
          color="bg-purple-50"
        />
      </div>

      <div className="bg-white rounded-lg shadow-sm p-6">
        <h2 className="text-lg font-semibold mb-4">Recent Activity</h2>
        {activities.length > 0 ? (
          <ul className="space-y-3">
            {activities.map((activity, index) => (
              <ActivityItem key={index} activity={activity} />
            ))}
          </ul>
        ) : (
          <div className="text-gray-500">No recent activities</div>
        )}
      </div>
    </div>
  );
}

function StatCard({ icon, title, value, color }) {
  return (
    <div className={`${color} rounded-lg p-6 flex items-center`}>
      <div className="mr-4 p-3 bg-white rounded-full shadow-sm">{icon}</div>
      <div>
        <p className="text-sm text-gray-500">{title}</p>
        <p className="text-2xl font-bold">{value}</p>
      </div>
    </div>
  );
}

function ActivityItem({ activity }) {
  const getActivityIcon = () => {
    switch (activity.type) {
      case "agent_added":
        return <FiUserPlus className="text-blue-500 mr-2" />;
      case "list_uploaded":
        return <FiFile className="text-green-500 mr-2" />;
      default:
        return <FiFileText className="text-gray-500 mr-2" />;
    }
  };

  const getActivityText = () => {
    switch (activity.type) {
      case "agent_added":
        return `New agent added: ${activity.data.name}`;
      case "list_uploaded":
        return `New list uploaded with ${activity.data.itemCount} items`;
      default:
        return activity.message;
    }
  };

  return (
    <li className="flex items-start">
      <div className="flex-shrink-0 mt-1">{getActivityIcon()}</div>
      <div className="ml-3">
        <p className="text-sm font-medium text-gray-900">{getActivityText()}</p>
        <p className="text-xs text-gray-500">
          {new Date(activity.timestamp).toLocaleString()}
        </p>
      </div>
    </li>
  );
}
